import React, { useEffect, useState } from "react";
import axios from "axios";

const UserProfile = () => {
    const [userData, setUserData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    useEffect(() => {
        // Retrieve user details from localStorage
        const role = localStorage.getItem("role");
        const username = localStorage.getItem("username");
        const userId = localStorage.getItem("userId");
        const email = localStorage.getItem("email");

        if (!role || !username || !userId || !email) {
            setError("User information not found.");
            setLoading(false);
            return;
        }

        // Prepare data to send to the backend
        const requestData = { role, username, userId, email };

        // Send request to backend
        axios.post("http://localhost:4000/api/profile", requestData)
            .then(response => {
                setUserData(response.data);
                setLoading(false);
            })
            .catch(error => {
                setError("Failed to fetch user data.");
                setLoading(false);
            });
    }, []);

    if (loading) return <p>Loading user data...</p>;
    if (error) return <p style={{ color: "red" }}>{error}</p>;

    return (
        <div>
            <h2>User Profile</h2>
            <p><strong>Username:</strong> {userData.username}</p>
            <p><strong>Email:</strong> {userData.email}</p>
            <p><strong>Role:</strong> {userData.role}</p>
            <p><strong>Employee Name:</strong> {userData.employee_name}</p>
            <p><strong>Phone:</strong> {userData.phone}</p>
            <p><strong>Project Name:</strong> {userData.project_name}</p>
        </div>
    );
};

export default UserProfile;
