import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [isMenuActive, setIsMenuActive] = useState(false);
  const [user, setUser] = useState(null); // Stores user details
  const [role, setRole] = useState(null); // Stores user role
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is logged in
    const storedUser = localStorage.getItem('username'); 
    const storedRole = localStorage.getItem('role'); // Fetch user role

    if (storedUser && storedRole) {
      setUser(storedUser);
      setRole(storedRole);
    }
  }, []);

  const toggleMenu = () => {
    setIsMenuActive(!isMenuActive);
  };

  const handleLogout = () => {
    localStorage.removeItem('token'); // Remove auth token
    localStorage.removeItem('username'); // Remove username
    localStorage.removeItem('role'); // Remove role
    setUser(null);
    setRole(null);
    navigate('/'); // Redirect to home page
  };

  const handleProfileNavigation = () => {
    if (role === "doctor") {
      navigate("/doctor-profile"); // Navigate to doctor profile
    } else {
      navigate("/user-profile"); // Navigate to user profile
    }
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-logo">HealthConnect</Link>
        <div className="hamburger" onClick={toggleMenu}>
          &#9776; {/* Hamburger icon */}
        </div>
        <ul className={`navbar-menu ${isMenuActive ? 'active' : ''}`}>
          <li className="navbar-item">
            <Link to="/" className="navbar-links">Home</Link>
          </li>
          <li className="navbar-item">
            <Link to="/about-us" className="navbar-links">About Us</Link>
          </li>
          <li className="navbar-item">
            <Link to="/blogs" className="navbar-links">Blogs</Link>
          </li>
          <li className="navbar-item">
            <Link to="/contact" className="navbar-links">Contact</Link>
          </li>

          {/* Show Login if no user is logged in */}
          {!user ? (
            <li className="navbar-item">
              <Link to="/login" className="navbar-links">Login</Link>
            </li>
          ) : (
            // Show username, user profile link, and logout button if logged in
            <li className="navbar-item dropdown">
              <span className="navbar-links profile-name">ðŸ‘¤ {user}</span>
              <button className="profile-button" onClick={handleProfileNavigation}>
                Profile
              </button>
              <button className="logout-button" onClick={handleLogout}>Logout</button>
            </li>
          )}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
