import React, { useEffect, useState } from "react";
import axios from "axios";

const DoctorList = ({ filters }) => {
  const [doctors, setDoctors] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  useEffect(() => {
    if (!filters || Object.keys(filters).length === 0) {
      return; // Avoid making API calls with empty filters
    }

    console.log("üîπ Filters sent to API:", filters);

    const fetchDoctors = async () => {
      setLoading(true);
      setError("");

      try {
        const response = await axios.get("http://localhost:4000/api/doctors/filter", {
          params: filters, 
        });

        if (response.data && Array.isArray(response.data)) {
          console.log("‚úÖ Fetched Doctors from API:", response.data);
          setDoctors(response.data);
        } else {
          setDoctors([]);
          setError("Unexpected API response format.");
        }
      } catch (err) {
        console.error("üö® Error fetching doctors:", err.message);
        setError(err.response?.data?.message || "Failed to fetch doctors.");
      } finally {
        setLoading(false);
      }
    };

    fetchDoctors();
  }, [filters]);

  return (
    <div>
      <h2>Doctor List</h2>

      {loading && <p>üîÑ Loading doctors...</p>}
      {error && <p style={{ color: "red" }}>‚ö† {error}</p>}

      {doctors.length > 0 ? (
        <table border="1">
          <thead>
            <tr>
              {doctors[0] &&
                Object.keys(doctors[0]).map((key) => (
                  <th key={key}>{key.replace(/_/g, " ")}</th>
                ))}
            </tr>
          </thead>
          <tbody>
            {doctors.map((doctor, index) => (
              <tr key={index}>
                {Object.values(doctor).map((value, i) => (
                  <td key={i}>{value}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        !loading && <p>‚ùå No doctors found.</p>
      )}
    </div>
  );
};

export default DoctorList;
