import React, { useState, useEffect } from "react";
import axios from "axios";
import Navbar from "../Common/Navbar";
import Footer from "../Common/Footer";
import HeaderBar from "../Common/HeaderBar";
import "./DoctorList.css";

const DoctorList = () => {
  const [doctors, setDoctors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const doctorsPerPage = 3;

  useEffect(() => {
    const fetchDoctors = async () => {
      try {
        setLoading(true);
        setError(null);

        const response = await axios.get("http://localhost:4000/api/doctors/filter");

        if (response.status === 200) {
          setDoctors(response.data);
        } else {
          setError("Failed to fetch doctors. Please try again.");
        }
      } catch (err) {
        console.error("ðŸš¨ Error fetching doctors:", err);
        setError("Error fetching doctors. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    fetchDoctors();
  }, []);

  // Pagination logic
  const indexOfLastDoctor = currentPage * doctorsPerPage;
  const indexOfFirstDoctor = indexOfLastDoctor - doctorsPerPage;
  const currentDoctors = doctors.slice(indexOfFirstDoctor, indexOfLastDoctor);

  return (
    <div>
      <HeaderBar />
      <Navbar />
      <div className="doctor-list-container">
        <h2>Doctor List</h2>
        {loading && <p>Loading doctors...</p>}
        {error && <p className="error-message">{error}</p>}
        {!loading && !error && currentDoctors.length > 0 ? (
          <div className="doctor-cards">
            {currentDoctors.map((doctor, index) => (
              <div key={index} className="doctor-card">
                <h3>{doctor.name}</h3>
                <p><strong>Specialty:</strong> {doctor.specialty}</p>
                <p><strong>Experience:</strong> {doctor.experience} years</p>
                <p><strong>Contact:</strong> {doctor.phone}</p>
                <p><strong>Email:</strong> {doctor.email}</p>

                {/* Display time slots */}
                <div className="time-slots">
                  <h4>Time Slots:</h4>

                  {(
                    doctor.Availability_Status_9_to_12 === "Available" ||
                    doctor.Availability_Status_2_to_5 === "Available" ||
                    doctor.Availability_Status_6_to_9 === "Available"
                  ) ? (
                    <>
                      {doctor.Availability_Status_9_to_12 === "Available" && (
                        <div className="time-slot">
                          <span>9 AM - 12 PM</span>
                          <button className="book-button">Book</button>
                        </div>
                      )}
                      {doctor.Availability_Status_2_to_5 === "Available" && (
                        <div className="time-slot">
                          <span>2 PM - 5 PM</span>
                          <button className="book-button">Book</button>
                        </div>
                      )}
                      {doctor.Availability_Status_6_to_9 === "Available" && (
                        <div className="time-slot">
                          <span>6 PM - 9 PM</span>
                          <button className="book-button">Book</button>
                        </div>
                      )}
                    </>
                  ) : (
                    <p>No time slots available</p>
                  )}
                </div>

              </div>
            ))}
          </div>
        ) : (
          !loading && !error && <p>No doctors found.</p>
        )}

        {/* Pagination Controls */}
        <div className="pagination-controls">
          <button 
            onClick={() => setCurrentPage(currentPage - 1)} 
            disabled={currentPage === 1}
          >
            Previous
          </button>
          <span>Page {currentPage}</span>
          <button 
            onClick={() => setCurrentPage(currentPage + 1)} 
            disabled={indexOfLastDoctor >= doctors.length}
          >
            Next
          </button>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default DoctorList;
