import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // For redirecting user
import './UserProfile.css';

const UserProfile = () => {
  const [userData, setUserData] = useState({
    username: '',
    email: '',
    role: '',
    userId: '',
    patientHistory: []
  });

  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is logged in by verifying token in localStorage
    const token = localStorage.getItem('token');

    if (!token) {
      // If no token is found, redirect to the login page
      navigate('/login');
      return;
    }

    // Fetch user details from the backend
    const fetchUserProfile = async () => {
      try {
        const response = await fetch('/api/user-profile', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`, // Send token for authorization
          },
        });

        if (response.ok) {
          const data = await response.json();
          setUserData({
            username: data.user.Username,
            email: data.user.Email,
            role: data.user.Role,
            userId: data.user.User_Id,
            patientHistory: data.history // The patient's history data
          });
        } else {
          navigate('/login'); // Redirect to login if fetching data fails
        }
      } catch (err) {
        console.error('Error fetching user profile:', err);
        navigate('/login'); // Redirect to login if error occurs
      }
    };

    fetchUserProfile();
  }, [navigate]);

  return (
    <div className="profile-container">
      <h2>User Profile</h2>
      <div className="profile-details">
        <div>
          <strong>Username:</strong> {userData.username}
        </div>
        <div>
          <strong>Email:</strong> {userData.email}
        </div>
        <div>
          <strong>Role:</strong> {userData.role}
        </div>
        <div>
          <strong>User ID:</strong> {userData.userId}
        </div>
      </div>

      {/* Display patient history */}
      {userData.patientHistory.length > 0 && (
        <div className="history-container">
          <h3>Patient History</h3>
          {userData.patientHistory.map((history, index) => (
            <div key={index} className="history-details">
              <div>
                <strong>Previous Diseases:</strong> {history.Previous_Diseases}
              </div>
              <div>
                <strong>Previous Symptoms:</strong> {history.Previous_Symptoms}
              </div>
              <div>
                <strong>Date of Record:</strong> {new Date(history.Date_Of_Record).toLocaleDateString()}
              </div>
              <div>
                <strong>Doctor ID:</strong> {history.Doctor_Id}
              </div>
              <div>
                <strong>Doctor Name:</strong> {history.Dr_Name}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UserProfile;
