import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // For redirecting user
import './UserProfile.css';

const UserProfile = () => {
  const [userData, setUserData] = useState({
    username: '',
    email: '',
    role: '',
    userId: ''
  });

  const navigate = useNavigate();

  useEffect(() => {
    // Fetch user details from localStorage
    const username = localStorage.getItem('username');
    const email = localStorage.getItem('email');
    const role = localStorage.getItem('role');
    const userId = localStorage.getItem('userId');

    if (username && email && role && userId) {
      setUserData({ username, email, role, userId });

      // Send user data to the backend
      const fetchUserProfile = async () => {
        try {
          const response = await fetch('http://localhost:4000/api/user-profile', {
            method: 'POST', // Using POST method to send the data
            headers: {
              'Content-Type': 'application/json', // Set header to JSON
            },
            body: JSON.stringify({
              username, 
              email, 
              role, 
              userId
            })
          });

          if (response.ok) {
            const data = await response.json();
            // Handle the response data, if needed
            console.log(data); // This will contain the data fetched from the backend
          } else {
            console.error('Failed to fetch user profile');
          }
        } catch (err) {
          console.error('Error sending user data:', err);
        }
      };

      fetchUserProfile(); // Call the function to send data to the backend
    } else {
      // If no user data is found, redirect to login
      navigate('/login');
    }
  }, [navigate]);

  return (
    <div className="profile-container">
      <h2>User Profile</h2>
      <div className="profile-details">
        <div>
          <strong>Username:</strong> {userData.username}
        </div>
        <div>
          <strong>Email:</strong> {userData.email}
        </div>
        <div>
          <strong>Role:</strong> {userData.role}
        </div>
        <div>
          <strong>User ID:</strong> {userData.userId}
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
