import React, { useState } from "react";
import { useLocation } from "react-router-dom"; // To get email from Register page
import "./DoctorDetails.css";
import Footer from "../Common/Footer";
import Navbar from "../Common/Navbar";
import HeaderBar from "../Common/HeaderBar";

const DoctorDetails = () => {
  const location = useLocation();
  const email = location.state?.email || ""; // Get email from Register page

  const [doctorData, setDoctorData] = useState({
    experience: "",
    location: "",
    speciality: "",
    contact_info: "",
    availability_status: "",
    consultation_fee: "",
    ratings: 4, // Default rating
    email: email, // Auto-filled from registration
  });

  const handleChange = (e) => {
    setDoctorData({ ...doctorData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:4000/api/doctor-info", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(doctorData),
      });

      const data = await response.json();
      if (response.ok) {
        alert("Doctor details saved successfully!");
      } else {
        alert(data.message || "Error saving details.");
      }
    } catch (error) {
      alert("Something went wrong.");
    }
  };

  return (
    <div>
      <HeaderBar />
      <Navbar />
      <div className="doctor-details-container">
        <h2>Complete Your Doctor Profile</h2>
        <form onSubmit={handleSubmit}>
          <input type="text" name="experience" placeholder="Experience (years)" onChange={handleChange} required />
          <input type="text" name="location" placeholder="Location" onChange={handleChange} required />
          <input type="text" name="speciality" placeholder="Speciality" onChange={handleChange} required />
          <input type="text" name="contact_info" placeholder="Contact Info" onChange={handleChange} required />
          <input type="text" name="availability_status" placeholder="Availability (e.g., 9AM-5PM)" onChange={handleChange} required />
          <input type="number" name="consultation_fee" placeholder="Consultation Fee ($)" onChange={handleChange} required />
          <button type="submit">Submit Details</button>
        </form>
      </div>
      <Footer />
    </div>
  );
};

export default DoctorDetails;
