import React, { useState } from "react";
import { useNavigate } from "react-router-dom"; // Redirect user after login
import axios from "axios"; // For API request
import "./Login.css";
import Navbar from '../Common/Navbar';
import Footer from '../Common/Footer';
import HeaderBar from '../Common/HeaderBar';

const Login = () => {
  const [credentials, setCredentials] = useState({ 
    email: "", 
    password: "", 
    role: "" // No default role
  });

  const [error, setError] = useState(""); // For displaying error messages
  const navigate = useNavigate(); // For navigation

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials({ ...credentials, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!credentials.role) {
      setError("Please select a role.");
      return;
    }

    try {
      // Send request to backend
      const response = await axios.post("http://localhost:3000/api/login", credentials);

      if (response.data.success) {
        alert(`Logged in as ${credentials.role} successfully!`);
        
        // Store token in localStorage (if using JWT authentication)
        localStorage.setItem("token", response.data.token);
        localStorage.setItem("role", credentials.role);

        // Redirect based on role
        if (credentials.role === "patient") {
          navigate("/patient-dashboard");
        } else if (credentials.role === "doctor") {
          navigate("/doctor-dashboard");
        }
      } else {
        setError("Invalid credentials. Please try again.");
      }
    } catch (error) {
      console.error("Login error:", error);
      setError("Login failed. Check your credentials and try again.");
    }
  };

  return (
    <div>
      <HeaderBar />
      <Navbar />
      <div className="login-container">
        <h2>Login</h2>
        {error && <p className="error-message">{error}</p>}
        <form onSubmit={handleSubmit}>
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={credentials.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={credentials.password}
            onChange={handleChange}
            required
          />
          <select name="role" value={credentials.role} onChange={handleChange} required>
            <option value="" disabled>Select Role</option>
            <option value="patient">Patient</option>
            <option value="doctor">Doctor</option>
          </select>
          <button type="submit">Login</button>
        </form>
      </div>
      <Footer />
    </div>
  );
};

export default Login;
