import React, { useState, useEffect } from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";

const DoctorList = () => {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);

  const [doctors, setDoctors] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchDoctors = async () => {
      try {
        setLoading(true);
        setError(null);

        const queryParams = {
          location: searchParams.get("location") || "",
          experience: searchParams.get("experience") || "",
          speciality: searchParams.get("speciality") || "",
          consultationFee: searchParams.get("consultationFee") || "",
          availabilityStatus: searchParams.get("availabilityStatus") || "",
        };

        const response = await axios.get("http://localhost:4000/api/filter", { params: queryParams });
        setDoctors(response.data);
      } catch (err) {
        console.error("ðŸš¨ Error fetching doctors:", err);
        setError("Error fetching doctors. Please try again.");
      } finally {
        setLoading(false);
      }
    };

    fetchDoctors();
  }, [location.search]); // Re-run when search query changes

  return (
    <div>
      <h2>Doctor List</h2>

      {loading && <p>Loading doctors...</p>}
      {error && <p style={{ color: "red" }}>{error}</p>}

      {!loading && !error && doctors.length > 0 ? (
        <table border="1">
          <thead>
            <tr>
              {Object.keys(doctors[0]).map((key) => (
                <th key={key}>{key.replace(/_/g, " ")}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {doctors.map((doctor, index) => (
              <tr key={index}>
                {Object.values(doctor).map((value, i) => (
                  <td key={i}>{value}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        !loading && !error && <p>No doctors found.</p>
      )}
    </div>
  );
};

export default DoctorList;
